select p.nombre, p.descripcion, c.id as id_carrito, c.id_cliente as id_usuario, cp.cantidad as cantidad,  p.id as id_producto, p.precio_iva as pvp, p.precio_iva * cp.cantidad as precio_total_producto from carrito as c join carrito_producto as cp on cp.id_carrito = c.id join productos as p on p.id = cp.id_producto where c.id = 10127;
select SUM(p.precio_iva * cp.cantidad) from carrito as c join carrito_producto as cp on cp.id_carrito = c.id join productos as p on p.id = cp.id_producto where c.id = 10141;
